pragma solidity ^0.8.23;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "./interfaces/IEscrowFactory.sol";
import "./interfaces/ILimitOrderProtocol.sol";
import "./libraries/ImmutablesLib.sol";
import "./libraries/TimelocksLib.sol";

/**
 * @title FusionPlusHub V2
 * @author 1Balancer Team
 * @notice Enhanced hub contract for Fusion+ cross-chain swaps on BASE with better 1inch integration
 * @dev Integrates with 1inch foundation contracts and manages cross-chain coordination
 */
contract FusionPlusHub_V2 is 
    Initializable, 
    AccessControlUpgradeable, 
    PausableUpgradeable, 
    ReentrancyGuardUpgradeable 
{
    // -- Constants --
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    bytes32 public constant RESOLVER_ROLE = keccak256("RESOLVER_ROLE");
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");

    // -- State --
    address public limitOrderProtocol;
    address public aggregationRouter;
    IEscrowFactory public escrowFactory;

    // Protocol fee configuration
    uint256 public protocolFee; // basis points (e.g., 30 = 0.3%)
    uint256 public constant MAX_FEE = 1000; // 10%
    address public feeRecipient;

    // Order tracking for cross-chain coordination
    mapping(bytes32 => OrderStatus) public orderStatuses;

    enum OrderStatus {
        None,
        Created,
        Filled,
        Completed,
        Cancelled
    }

    // -- Events --
    event FusionPlusOrderCreated(
        bytes32 indexed orderHash,
        address indexed maker,
        bytes32 destinationChain,
        address srcToken,
        address dstToken,
        uint256 srcAmount,
        uint256 dstAmount
    );

    event OrderStatusUpdated(
        bytes32 indexed orderHash,
        OrderStatus status
    );

    event ProtocolFeeUpdated(uint256 newFee);
    event FeeRecipientUpdated(address newRecipient);
    event ContractsUpdated(
        address limitOrderProtocol,
        address aggregationRouter,
        address escrowFactory
    );

    // -- Errors --
    error FusionPlusHub__InvalidAddress();
    error FusionPlusHub__InvalidFee();
    error FusionPlusHub__UnauthorizedAccess();
    error FusionPlusHub__InvalidAmount();
    error FusionPlusHub__OrderAlreadyExists();
    error FusionPlusHub__InvalidOrderStatus();

    // -- Modifier --
    modifier onlyAdmin() {
        if (!hasRole(DEFAULT_ADMIN_ROLE, msg.sender)) {
            revert FusionPlusHub__UnauthorizedAccess();
        }
        _;
    }

    modifier validAddress(address addr) {
        if (addr == address(0)) {
            revert FusionPlusHub__InvalidAddress();
        }
        _;
    }

    // -- Initializer --
    function initialize(
        address _limitOrderProtocol,
        address _aggregationRouter,
        address _escrowFactory
    ) public initializer {
        __AccessControl_init();
        __Pausable_init();
        __ReentrancyGuard_init();

        // Validate addresses
        if (_limitOrderProtocol == address(0) || 
            _aggregationRouter == address(0) ||
            _escrowFactory == address(0)) {
            revert FusionPlusHub__InvalidAddress();
        }

        // Set foundation contracts
        limitOrderProtocol = _limitOrderProtocol;
        aggregationRouter = _aggregationRouter;
        escrowFactory = IEscrowFactory(_escrowFactory);

        // Grant roles
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(OPERATOR_ROLE, msg.sender);
        _grantRole(RESOLVER_ROLE, msg.sender);
        _grantRole(UPGRADER_ROLE, msg.sender);

        // Set initial fee configuration
        protocolFee = 30; // 0.3%
        feeRecipient = msg.sender;
    }

    // -- External Functions --

    /**
     * @notice Creates a cross-chain Fusion+ order with proper validation
     * @dev Validates amounts and creates order hash for tracking
     * @param destinationChain The destination chain identifier
     * @param srcToken Source chain token address
     * @param dstToken Destination chain token address
     * @param srcAmount Amount on source chain
     * @param dstAmount Expected amount on destination chain
     * @param orderData Additional order parameters
     */
    function createFusionPlusOrder(
        bytes32 destinationChain,
        address srcToken,
        address dstToken,
        uint256 srcAmount,
        uint256 dstAmount,
        bytes calldata orderData
    ) external nonReentrant whenNotPaused returns (bytes32 orderHash) {
        // Validate inputs
        if (srcAmount == 0 || dstAmount == 0) {
            revert FusionPlusHub__InvalidAmount();
        }

        if (srcToken == address(0) || dstToken == address(0)) {
            revert FusionPlusHub__InvalidAddress();
        }

        // Generate unique order hash
        orderHash = keccak256(abi.encodePacked(
            msg.sender,
            destinationChain,
            srcToken,
            dstToken,
            srcAmount,
            dstAmount,
            block.timestamp,
            block.number
        ));

        // Check if order already exists
        if (orderStatuses[orderHash] != OrderStatus.None) {
            revert FusionPlusHub__OrderAlreadyExists();
        }

        // Mark order as created
        orderStatuses[orderHash] = OrderStatus.Created;

        // Emit event for off-chain orchestration service
        emit FusionPlusOrderCreated(
            orderHash,
            msg.sender,
            destinationChain,
            srcToken,
            dstToken,
            srcAmount,
            dstAmount
        );

        emit OrderStatusUpdated(orderHash, OrderStatus.Created);
    }

    /**
     * @notice Updates order status (called by resolver/orchestrator)
     * @param orderHash The order hash to update
     * @param newStatus The new status
     */
    function updateOrderStatus(
        bytes32 orderHash,
        OrderStatus newStatus
    ) external onlyRole(RESOLVER_ROLE) {
        OrderStatus currentStatus = orderStatuses[orderHash];

        // Validate status transition
        if (currentStatus == OrderStatus.None) {
            revert FusionPlusHub__InvalidOrderStatus();
        }

        if (currentStatus == OrderStatus.Cancelled || 
            currentStatus == OrderStatus.Completed) {
            revert FusionPlusHub__InvalidOrderStatus();
        }

        orderStatuses[orderHash] = newStatus;
        emit OrderStatusUpdated(orderHash, newStatus);
    }

    /**
     * @notice Creates escrow pair for cross-chain atomic swap
     * @dev Called by resolver after order validation
     */
    function createEscrowsForOrder(
        bytes32 orderHash,
        ImmutablesLib.Immutables calldata srcImmutables,
        ImmutablesLib.Immutables calldata dstImmutables
    ) external payable onlyRole(RESOLVER_ROLE) returns (
        address srcEscrow,
        address dstEscrow
    ) {
        // Verify order exists and is in correct state
        if (orderStatuses[orderHash] != OrderStatus.Created) {
            revert FusionPlusHub__InvalidOrderStatus();
        }

        // Create escrows through factory
        (srcEscrow, dstEscrow) = escrowFactory.createEscrowPair{value: msg.value}(
            srcImmutables,
            dstImmutables
        );

        // Update order status
        orderStatuses[orderHash] = OrderStatus.Filled;
        emit OrderStatusUpdated(orderHash, OrderStatus.Filled);
    }

    // -- Admin Functions --

    /**
     * @notice Updates protocol fee
     * @param _fee New fee in basis points
     */
    function setProtocolFee(uint256 _fee) external onlyAdmin {
        if (_fee > MAX_FEE) {
            revert FusionPlusHub__InvalidFee();
        }
        protocolFee = _fee;
        emit ProtocolFeeUpdated(_fee);
    }

    /**
     * @notice Updates fee recipient
     * @param _recipient New fee recipient address
     */
    function setFeeRecipient(address _recipient) external onlyAdmin validAddress(_recipient) {
        feeRecipient = _recipient;
        emit FeeRecipientUpdated(_recipient);
    }

    /**
     * @notice Updates contract addresses
     * @param _limitOrderProtocol New limit order protocol address
     * @param _aggregationRouter New aggregation router address
     * @param _escrowFactory New escrow factory address
     */
    function updateContracts(
        address _limitOrderProtocol,
        address _aggregationRouter,
        address _escrowFactory
    ) external onlyAdmin {
        if (_limitOrderProtocol != address(0)) {
            limitOrderProtocol = _limitOrderProtocol;
        }
        if (_aggregationRouter != address(0)) {
            aggregationRouter = _aggregationRouter;
        }
        if (_escrowFactory != address(0)) {
            escrowFactory = IEscrowFactory(_escrowFactory);
        }

        emit ContractsUpdated(
            limitOrderProtocol,
            aggregationRouter,
            _escrowFactory
        );
    }

    /**
     * @notice Pauses the contract
     */
    function pause() external onlyRole(OPERATOR_ROLE) {
        _pause();
    }

    /**
     * @notice Unpauses the contract
     */
    function unpause() external onlyRole(OPERATOR_ROLE) {
        _unpause();
    }

    // -- View Functions --

    /**
     * @notice Calculates fee for an amount
     * @param amount The amount to calculate fee for
     * @return The fee amount
     */
    function calculateFee(uint256 amount) public view returns (uint256) {
        return (amount * protocolFee) / 10000;
    }

    /**
     * @notice Gets order status
     * @param orderHash The order hash to check
     * @return The order status
     */
    function getOrderStatus(bytes32 orderHash) external view returns (OrderStatus) {
        return orderStatuses[orderHash];
    }

    /**
     * @notice Gets all contract addresses
     * @return _limitOrderProtocol The limit order protocol address
     * @return _aggregationRouter The aggregation router address
     * @return _escrowFactory The escrow factory address
     */
    function getContractAddresses() external view returns (
        address _limitOrderProtocol,
        address _aggregationRouter,
        address _escrowFactory
    ) {
        return (
            limitOrderProtocol,
            aggregationRouter,
            address(escrowFactory)
        );
    }
}","keccak256":"0xb1bb6532c97f387ee09ad901c28a28ed3b7df8693722f2dac8dcb94fc1fa815a","license":"MIT"},"contracts/ethereum-hub/interfaces/IEscrowFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import "../libraries/ImmutablesLib.sol";

/**
 * @title IEscrowFactory
 * @notice Interface for 1inch-compatible escrow factory
 * @dev Creates escrows for cross-chain atomic swaps following 1inch patterns
 */
interface IEscrowFactory {

    /**
     * @notice Creates a source chain escrow
     * @param immutables Escrow parameters
     * @return escrow Address of created escrow
     */
    function createSrcEscrow(ImmutablesLib.Immutables calldata immutables) 
        external 
        payable 
        returns (address escrow);

    /**
     * @notice Creates a destination chain escrow
     * @param immutables Escrow parameters
     * @param srcCancellationTimestamp Source chain cancellation timestamp
     * @return escrow Address of created escrow
     */
    function createDstEscrow(
        ImmutablesLib.Immutables calldata immutables,
        uint256 srcCancellationTimestamp
    ) external payable returns (address escrow);

    /**
     * @notice Computes the address of a source escrow
     * @param immutables Escrow parameters
     * @return escrowAddress Computed escrow address
     */
    function addressOfEscrowSrc(ImmutablesLib.Immutables calldata immutables) 
        external 
        view 
        returns (address escrowAddress);

    /**
     * @notice Computes the address of a destination escrow
     * @param immutables Escrow parameters
     * @return escrowAddress Computed escrow address
     */
    function addressOfEscrowDst(ImmutablesLib.Immutables calldata immutables)
        external
        view
        returns (address escrowAddress);

    /**
     * @notice Creates escrows for orchestration service
     * @dev Batch creation for efficiency
     * @param srcImmutables Source chain immutables
     * @param dstImmutables Destination chain immutables
     * @return srcEscrow Source escrow address
     * @return dstEscrow Destination escrow address
     */
    function createEscrowPair(
        ImmutablesLib.Immutables calldata srcImmutables,
        ImmutablesLib.Immutables calldata dstImmutables
    ) external payable returns (
        address srcEscrow,
        address dstEscrow
    );

    /**
     * @notice Checks if an address is a valid escrow created by this factory
     * @param escrow Address to check
     * @return True if the address is a valid escrow
     */
    function isValidEscrow(address escrow) external view returns (bool);

    /**
     * @notice Emitted when source escrow is created
     */
    event SrcEscrowCreated(
        address indexed escrow,
        address indexed maker,
        address indexed taker,
        bytes32 hashlockHash
    );

    /**
     * @notice Emitted when destination escrow is created
     */
    event DstEscrowCreated(
        address indexed escrow,
        address indexed maker,
        address indexed taker,
        bytes32 hashlockHash
    );
}","keccak256":"0x9fb663f17a1b8ed0235701ee5939e18a90adaf8685d079864af09ea096b91e14","license":"MIT"},"contracts/ethereum-hub/interfaces/ILimitOrderProtocol.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

/**
 * @title ILimitOrderProtocol
 * @notice Interface for 1inch Limit Order Protocol integration
 * @dev This interface enables cross-chain swaps through 1inch foundation
 */
interface ILimitOrderProtocol {
    struct Order {
        uint256 salt;
        address makerAsset;
        address takerAsset;
        address maker;
        address receiver;
        address allowedSender;  // 0x0 for public orders
        uint256 makingAmount;
        uint256 takingAmount;
        uint256 offsets;
        bytes interactions;     // Encoded interactions for hooks
    }

    struct Signature {
        bytes32 r;
        bytes32 vs;
    }

    /**
     * @notice Fills a limit order
     * @param order The order to fill
     * @param signature Order signature
     * @param interaction Additional interaction data
     * @param makingAmount Amount of maker asset
     * @param takingAmount Amount of taker asset
     * @param skipPermitAndThresholdAmount Skip permit and threshold checks
     * @return actualMakingAmount Actual amount of maker asset filled
     * @return actualTakingAmount Actual amount of taker asset filled
     * @return orderHash Hash of the filled order
     */
    function fillOrder(
        Order calldata order,
        bytes calldata signature,
        bytes calldata interaction,
        uint256 makingAmount,
        uint256 takingAmount,
        uint256 skipPermitAndThresholdAmount
    ) external payable returns (
        uint256 actualMakingAmount,
        uint256 actualTakingAmount,
        bytes32 orderHash
    );

    /**
     * @notice Fills an order with compact signature
     * @param order The order to fill
     * @param r Signature r value
     * @param vs Signature vs value
     * @param amount Fill amount
     * @param takerTraits Taker traits for advanced features
     * @param args Additional arguments
     */
    function fillOrderArgs(
        Order calldata order,
        bytes32 r,
        bytes32 vs,
        uint256 amount,
        uint256 takerTraits,
        bytes calldata args
    ) external payable returns (
        uint256 actualMakingAmount,
        uint256 actualTakingAmount,
        bytes32 orderHash
    );

    /**
     * @notice Cancels an order
     * @param orderInfo Order info for cancellation
     */
    function cancelOrder(uint256 orderInfo) external;

    /**
     * @notice Checks if an order is valid
     * @param order The order to check
     * @param signature Order signature
     * @return isValid True if order is valid
     */
    function checkOrder(Order calldata order, bytes calldata signature)
        external
        view
        returns (bool isValid);

    /**
     * @notice Gets remaining fill amount for an order
     * @param orderHash Hash of the order
     * @return remaining Remaining amount that can be filled
     */
    function remaining(bytes32 orderHash) external view returns (uint256);

    /**
     * @notice Emitted when an order is filled
     */
    event OrderFilled(
        bytes32 indexed orderHash,
        address indexed maker,
        address indexed taker,
        uint256 makingAmount,
        uint256 takingAmount
    );

    /**
     * @notice Emitted when an order is cancelled
     */
    event OrderCancelled(bytes32 indexed orderHash);
}","keccak256":"0xe44b1bcb5fdd8ce2c611af2df9e675572d83ae9ee4e691ba2f55a2285349598a","license":"MIT"},"contracts/ethereum-hub/libraries/ImmutablesLib.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import "./TimelocksLib.sol";

/**
 * @title ImmutablesLib
 * @notice Library for managing escrow immutable parameters
 * @dev Based on 1inch cross-chain-swap pattern for parameter encoding
 */
library ImmutablesLib {
    using TimelocksLib for TimelocksLib.Timelocks;

    struct Immutables {
        address maker;
        address taker;
        address token;
        uint256 amount;
        uint256 safetyDeposit;
        bytes32 hashlockHash;
        TimelocksLib.Timelocks timelocks;
        bytes32 orderHash;       // Link to 1inch order
        uint256 chainId;         // Target chain for cross-chain swaps
    }

    /**
     * @notice Hashes the immutable parameters for escrow address calculation
     * @param immutables The immutable parameters
     * @return hash The hash of the parameters
     */
    function hash(Immutables memory immutables) internal pure returns (bytes32) {
        return keccak256(abi.encode(
            immutables.maker,
            immutables.taker,
            immutables.token,
            immutables.amount,
            immutables.safetyDeposit,
            immutables.hashlockHash,
            immutables.timelocks.encode(),
            immutables.orderHash,
            immutables.chainId
        ));
    }

    /**
     * @notice Validates immutable parameters
     * @param immutables The parameters to validate
     * @return valid True if parameters are valid
     */
    function validate(Immutables calldata immutables) internal view returns (bool valid) {
        valid = immutables.maker != address(0) &&
                immutables.taker != address(0) &&
                immutables.token != address(0) &&
                immutables.amount > 0 &&
                immutables.hashlockHash != bytes32(0) &&
                immutables.timelocks.validate() &&
                immutables.chainId > 0;
    }

    /**
     * @notice Creates immutables for source chain escrow
     * @param maker The maker address
     * @param taker The taker address
     * @param token The token address
     * @param amount The token amount
     * @param hashlockHash The hashlock hash
     * @param duration The swap duration
     * @param orderHash The 1inch order hash
     * @param destinationChainId The destination chain ID
     * @return immutables The created immutables
     */
    function createForSource(
        address maker,
        address taker,
        address token,
        uint256 amount,
        bytes32 hashlockHash,
        uint32 duration,
        bytes32 orderHash,
        uint256 destinationChainId
    ) internal view returns (Immutables memory immutables) {
        immutables = Immutables({
            maker: maker,
            taker: taker,
            token: token,
            amount: amount,
            safetyDeposit: calculateSafetyDeposit(amount),
            hashlockHash: hashlockHash,
            timelocks: TimelocksLib.create(duration),
            orderHash: orderHash,
            chainId: destinationChainId
        });
    }

    /**
     * @notice Creates immutables for destination chain escrow
     * @param sourceImmutables The source chain immutables
     * @param destinationToken The token on destination chain
     * @param destinationAmount The amount on destination chain
     * @return immutables The created immutables
     */
    function createForDestination(
        Immutables memory sourceImmutables,
        address destinationToken,
        uint256 destinationAmount
    ) internal view returns (Immutables memory immutables) {
        // Swap maker and taker for destination chain
        immutables = Immutables({
            maker: sourceImmutables.taker,  // Taker becomes maker on destination
            taker: sourceImmutables.maker,  // Maker becomes taker on destination
            token: destinationToken,
            amount: destinationAmount,
            safetyDeposit: calculateSafetyDeposit(destinationAmount),
            hashlockHash: sourceImmutables.hashlockHash,  // Same hashlock
            timelocks: sourceImmutables.timelocks,        // Same timelocks
            orderHash: sourceImmutables.orderHash,         // Same order hash
            chainId: block.chainid                         // Current chain ID
        });
    }

    /**
     * @notice Calculates safety deposit amount (0.1% of swap amount)
     * @param amount The swap amount
     * @return deposit The safety deposit amount
     */
    function calculateSafetyDeposit(uint256 amount) internal pure returns (uint256 deposit) {
        deposit = amount / 1000; // 0.1%
        if (deposit == 0) {
            deposit = 1; // Minimum 1 wei
        }
    }

    /**
     * @notice Encodes immutables for efficient storage/transmission
     * @param immutables The immutables to encode
     * @return encoded The encoded immutables
     */
    function encode(Immutables memory immutables) internal pure returns (bytes memory encoded) {
        encoded = abi.encode(
            immutables.maker,
            immutables.taker,
            immutables.token,
            immutables.amount,
            immutables.safetyDeposit,
            immutables.hashlockHash,
            immutables.timelocks.encode(),
            immutables.orderHash,
            immutables.chainId
        );
    }

    /**
     * @notice Decodes immutables from bytes
     * @param encoded The encoded immutables
     * @return immutables The decoded immutables
     */
    function decode(bytes memory encoded) internal pure returns (Immutables memory immutables) {
        // Decode in two steps to avoid stack too deep
        address maker;
        address taker;
        address token;
        uint256 amount;
        uint256 safetyDeposit;

        (maker, taker, token, amount, safetyDeposit) = abi.decode(
            encoded, 
            (address, address, address, uint256, uint256)
        );

        // Decode remaining fields
        bytes32 hashlockHash;
        bytes32 timelocksEncoded;
        bytes32 orderHash;
        uint256 chainId;

        assembly {
            let dataPtr := add(encoded, 0x20)
            hashlockHash := mload(add(dataPtr, 0xA0))
            timelocksEncoded := mload(add(dataPtr, 0xC0))
            orderHash := mload(add(dataPtr, 0xE0))
            chainId := mload(add(dataPtr, 0x100))
        }

        immutables = Immutables({
            maker: maker,
            taker: taker,
            token: token,
            amount: amount,
            safetyDeposit: safetyDeposit,
            hashlockHash: hashlockHash,
            timelocks: TimelocksLib.decode(timelocksEncoded),
            orderHash: orderHash,
            chainId: chainId
        });
    }

    /**
     * @notice Checks if the maker can withdraw (during withdrawal window)
     * @param immutables The immutables to check
     * @param isSource True if checking source chain
     * @return canWithdraw True if maker can withdraw
     */
    function makerCanWithdraw(
        Immutables memory immutables,
        bool isSource
    ) internal view returns (bool) {
        return immutables.timelocks.canWithdraw(isSource, false);
    }

    /**
     * @notice Checks if anyone can withdraw (public withdrawal phase)
     * @param immutables The immutables to check
     * @param isSource True if checking source chain
     * @return canWithdraw True if public withdrawal is allowed
     */
    function publicCanWithdraw(
        Immutables memory immutables,
        bool isSource
    ) internal view returns (bool) {
        return immutables.timelocks.canWithdraw(isSource, true);
    }

    /**
     * @notice Checks if the escrow can be cancelled
     * @param immutables The immutables to check
     * @param isSource True if checking source chain
     * @return canCancel True if cancellation is allowed
     */
    function canCancel(
        Immutables memory immutables,
        bool isSource
    ) internal view returns (bool) {
        return immutables.timelocks.canCancel(isSource);
    }
}","keccak256":"0x48eb27b6fedcfa8013b912932e0a2d39be4470c691d91d478fee9c0a368709bf","license":"MIT"},"contracts/ethereum-hub/libraries/TimelocksLib.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

/**
 * @title TimelocksLib
 * @notice Library for managing cross-chain timelock coordination
 * @dev Based on 1inch cross-chain-swap timelock pattern
 */
library TimelocksLib {
    struct Timelocks {
        uint32 srcWithdrawal;        // Withdrawal time on source chain
        uint32 srcPublicWithdrawal;  // Public withdrawal time on source chain
        uint32 srcCancellation;      // Cancellation time on source chain
        uint32 srcDeployedAt;        // Deployment timestamp on source chain
        uint32 dstWithdrawal;        // Withdrawal time on destination chain
        uint32 dstCancellation;      // Cancellation time on destination chain
        uint32 dstDeployedAt;        // Deployment timestamp on destination chain
    }

    // Constants for timeout calculations
    uint32 private constant MIN_CROSS_CHAIN_DELAY = 30 minutes;
    uint32 private constant SAFETY_BUFFER = 2 hours;
    uint32 private constant MAX_DURATION = 7 days;

    /**
     * @notice Creates timelocks for cross-chain atomic swap
     * @param baseDuration Base duration for the swap
     * @return timelocks Configured timelock structure
     */
    function create(uint32 baseDuration) internal view returns (Timelocks memory timelocks) {
        require(baseDuration >= MIN_CROSS_CHAIN_DELAY, "Duration too short");
        require(baseDuration <= MAX_DURATION, "Duration too long");

        uint32 currentTime = uint32(block.timestamp);

        // Source chain timelocks
        timelocks.srcDeployedAt = currentTime;
        timelocks.srcWithdrawal = currentTime + baseDuration;
        timelocks.srcPublicWithdrawal = currentTime + baseDuration + SAFETY_BUFFER;
        timelocks.srcCancellation = currentTime + baseDuration + (2 * SAFETY_BUFFER);

        // Destination chain timelocks (shorter to ensure atomic execution)
        timelocks.dstDeployedAt = currentTime;
        timelocks.dstWithdrawal = currentTime + (baseDuration / 2);
        timelocks.dstCancellation = currentTime + baseDuration - SAFETY_BUFFER;
    }

    /**
     * @notice Validates timelocks for safety
     * @param timelocks Timelocks to validate
     * @return valid True if timelocks are safe for cross-chain execution
     */
    function validate(Timelocks memory timelocks) internal view returns (bool valid) {
        uint32 currentTime = uint32(block.timestamp);

        // Basic timestamp ordering validation
        valid = timelocks.srcWithdrawal > timelocks.srcDeployedAt &&
                timelocks.srcPublicWithdrawal > timelocks.srcWithdrawal &&
                timelocks.srcCancellation > timelocks.srcPublicWithdrawal &&
                timelocks.dstWithdrawal > timelocks.dstDeployedAt &&
                timelocks.dstCancellation > timelocks.dstWithdrawal &&
                // Cross-chain safety: dst cancellation before src withdrawal
                timelocks.dstCancellation < timelocks.srcWithdrawal &&
                // Current time validation
                currentTime >= timelocks.srcDeployedAt;
    }

    /**
     * @notice Sets the deployed timestamp for a chain
     * @param timelocks Timelocks structure
     * @param timestamp Deployment timestamp
     * @param isSource True for source chain, false for destination
     */
    function setDeployedAt(
        Timelocks memory timelocks,
        uint32 timestamp,
        bool isSource
    ) internal pure returns (Timelocks memory) {
        if (isSource) {
            timelocks.srcDeployedAt = timestamp;
        } else {
            timelocks.dstDeployedAt = timestamp;
        }
        return timelocks;
    }

    /**
     * @notice Checks if withdrawal is allowed
     * @param timelocks Timelocks structure
     * @param isSource True for source chain, false for destination
     * @param isPublic True for public withdrawal phase
     * @return allowed True if withdrawal is allowed
     */
    function canWithdraw(
        Timelocks memory timelocks,
        bool isSource,
        bool isPublic
    ) internal view returns (bool allowed) {
        uint32 currentTime = uint32(block.timestamp);

        if (isSource) {
            if (isPublic) {
                allowed = currentTime >= timelocks.srcPublicWithdrawal &&
                         currentTime < timelocks.srcCancellation;
            } else {
                allowed = currentTime >= timelocks.srcWithdrawal &&
                         currentTime < timelocks.srcCancellation;
            }
        } else {
            allowed = currentTime >= timelocks.dstWithdrawal &&
                     currentTime < timelocks.dstCancellation;
        }
    }

    /**
     * @notice Checks if cancellation is allowed
     * @param timelocks Timelocks structure
     * @param isSource True for source chain, false for destination
     * @return allowed True if cancellation is allowed
     */
    function canCancel(
        Timelocks memory timelocks,
        bool isSource
    ) internal view returns (bool allowed) {
        uint32 currentTime = uint32(block.timestamp);

        if (isSource) {
            allowed = currentTime >= timelocks.srcCancellation;
        } else {
            allowed = currentTime >= timelocks.dstCancellation;
        }
    }

    /**
     * @notice Encodes timelocks into bytes32 for efficient storage
     * @param timelocks Timelocks to encode
     * @return encoded Encoded timelocks
     */
    function encode(Timelocks memory timelocks) internal pure returns (bytes32 encoded) {
        encoded = bytes32(
            (uint256(timelocks.srcWithdrawal) << 224) |
            (uint256(timelocks.srcPublicWithdrawal) << 192) |
            (uint256(timelocks.srcCancellation) << 160) |
            (uint256(timelocks.srcDeployedAt) << 128) |
            (uint256(timelocks.dstWithdrawal) << 96) |
            (uint256(timelocks.dstCancellation) << 64) |
            (uint256(timelocks.dstDeployedAt) << 32)
        );
    }

    /**
     * @notice Decodes timelocks from bytes32
     * @param encoded Encoded timelocks
     * @return timelocks Decoded timelocks
