        timelocks.srcDeployedAt = uint32(uint256(encoded) >> 128);
        timelocks.dstWithdrawal = uint32(uint256(encoded) >> 96);
        timelocks.dstCancellation = uint32(uint256(encoded) >> 64);
        timelocks.dstDeployedAt = uint32(uint256(encoded) >> 32);
    }
