// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "../interfaces/IEscrowFactory.sol";
import "../interfaces/ILimitOrderProtocol.sol";
import "../libraries/ImmutablesLib.sol";
import "../libraries/TimelocksLib.sol";

/**
 * @title BaseEscrowFactory
 * @notice Factory for creating escrow contracts following 1inch pattern
 * @dev Designed to work with orchestration service for cross-chain coordination
 */
abstract contract BaseEscrowFactory is IEscrowFactory, AccessControl, Pausable {
    using ImmutablesLib for ImmutablesLib.Immutables;
    using TimelocksLib for TimelocksLib.Timelocks;

    // -- Constants --
    bytes32 public constant ORCHESTRATOR_ROLE = keccak256("ORCHESTRATOR_ROLE");
    bytes32 public constant RESOLVER_ROLE = keccak256("RESOLVER_ROLE");

    // -- State --
    address public immutable escrowSrcImplementation;
    address public immutable escrowDstImplementation;
    address public immutable limitOrderProtocol;

    // Track deployed escrows for orchestration service
    mapping(bytes32 => address) public escrows; // orderHash => escrow address
    mapping(address => bool) public isValidEscrow;

    // -- Errors --
    error InvalidImmutables();
    error EscrowAlreadyExists();
    error InvalidSafetyDeposit();
    error UnauthorizedCaller();

    // -- Constructor --
    constructor(
        address _escrowSrcImpl,
        address _escrowDstImpl,
        address _limitOrderProtocol
    ) {
        require(_escrowSrcImpl != address(0), "Invalid src implementation");
        require(_escrowDstImpl != address(0), "Invalid dst implementation");
        require(_limitOrderProtocol != address(0), "Invalid LOP address");

        escrowSrcImplementation = _escrowSrcImpl;
        escrowDstImplementation = _escrowDstImpl;
        limitOrderProtocol = _limitOrderProtocol;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ORCHESTRATOR_ROLE, msg.sender);
    }

    /**
     * @notice Creates a source chain escrow
     * @dev Called by orchestration service when initiating cross-chain swap
     * @param immutables Escrow parameters
     * @return escrow Address of created escrow
     */
    function createSrcEscrow(
        ImmutablesLib.Immutables calldata immutables
    ) external payable override onlyRole(ORCHESTRATOR_ROLE) whenNotPaused returns (address escrow) {
        // Validate immutables
        if (!ImmutablesLib.validate(immutables)) {
            revert InvalidImmutables();
        }

        // Validate safety deposit
        if (msg.value != immutables.safetyDeposit) {
            revert InvalidSafetyDeposit();
        }

        // Check if escrow already exists for this order
        bytes32 immutablesHash = ImmutablesLib.hash(immutables);
        if (escrows[immutablesHash] != address(0)) {
            revert EscrowAlreadyExists();
        }

        // Deploy escrow using CREATE2 for deterministic address
        escrow = Clones.cloneDeterministic(escrowSrcImplementation, immutablesHash);

        // Initialize escrow
        (bool success,) = escrow.call{value: msg.value}(
            abi.encodeWithSignature("initialize(bytes)", immutables.encode())
        );
        require(success, "Escrow initialization failed");

        // Track escrow
        escrows[immutablesHash] = escrow;
        isValidEscrow[escrow] = true;

        emit SrcEscrowCreated(
            escrow,
            immutables.maker,
            immutables.taker,
            immutables.hashlockHash
        );
    }

    /**
     * @notice Creates a destination chain escrow
     * @dev Called by orchestration service on destination chain
     * @param immutables Escrow parameters (adapted for destination)
     * @param srcCancellationTimestamp Source chain cancellation timestamp
     * @return escrow Address of created escrow
     */
    function createDstEscrow(
        ImmutablesLib.Immutables calldata immutables,
        uint256 srcCancellationTimestamp
    ) external payable override onlyRole(ORCHESTRATOR_ROLE) whenNotPaused returns (address escrow) {
        // Validate immutables
        if (!ImmutablesLib.validate(immutables)) {
            revert InvalidImmutables();
        }

        // Validate safety deposit
        if (msg.value != immutables.safetyDeposit) {
            revert InvalidSafetyDeposit();
        }

        // Validate timing coordination
        require(
            immutables.timelocks.dstCancellation < srcCancellationTimestamp,
            "Invalid timeout coordination"
        );

        // Check if escrow already exists
        bytes32 immutablesHash = ImmutablesLib.hash(immutables);
        if (escrows[immutablesHash] != address(0)) {
            revert EscrowAlreadyExists();
        }

        // Deploy escrow
        escrow = Clones.cloneDeterministic(escrowDstImplementation, immutablesHash);

        // Initialize escrow
        (bool success,) = escrow.call{value: msg.value}(
            abi.encodeWithSignature("initialize(bytes,uint256)", 
                immutables.encode(), 
                srcCancellationTimestamp
            )
        );
        require(success, "Escrow initialization failed");

        // Track escrow
        escrows[immutablesHash] = escrow;
        isValidEscrow[escrow] = true;

        emit DstEscrowCreated(
            escrow,
            immutables.maker,
            immutables.taker,
            immutables.hashlockHash
        );
    }

    /**
     * @notice Computes the address of a source escrow
     * @dev Used by orchestration service for coordination
     * @param immutables Escrow parameters
     * @return escrowAddress Computed escrow address
     */
    function addressOfEscrowSrc(
        ImmutablesLib.Immutables calldata immutables
    ) external view override returns (address escrowAddress) {
        bytes32 immutablesHash = ImmutablesLib.hash(immutables);
        escrowAddress = Clones.predictDeterministicAddress(
            escrowSrcImplementation,
            immutablesHash
        );
    }

    /**
     * @notice Computes the address of a destination escrow
     * @dev Used by orchestration service for coordination
     * @param immutables Escrow parameters
     * @return escrowAddress Computed escrow address
     */
    function addressOfEscrowDst(
        ImmutablesLib.Immutables calldata immutables
    ) external view override returns (address escrowAddress) {
        bytes32 immutablesHash = ImmutablesLib.hash(immutables);
        escrowAddress = Clones.predictDeterministicAddress(
            escrowDstImplementation,
            immutablesHash
        );
    }

    /**
     * @notice Fills a 1inch limit order as part of cross-chain swap
     * @dev Called by resolver to initiate the swap
     * @param order The limit order to fill
     * @param signature Order signature
     * @param makingAmount Amount to make
     * @param takingAmount Amount to take
     * @param escrowAddress Target escrow for funds
     */
    function fillOrderWithEscrow(
        ILimitOrderProtocol.Order calldata order,
        bytes calldata signature,
        uint256 makingAmount,
        uint256 takingAmount,
        address escrowAddress
    ) external onlyRole(RESOLVER_ROLE) whenNotPaused {
        require(isValidEscrow[escrowAddress], "Invalid escrow");

        // Fill order with escrow as receiver
        // The escrow will handle the cross-chain coordination
        bytes memory interaction = abi.encode(escrowAddress);

        ILimitOrderProtocol(limitOrderProtocol).fillOrder(
            order,
            signature,
            interaction,
            makingAmount,
            takingAmount,
            0 // skipPermitAndThresholdAmount
        );
    }

    // -- Admin Functions --

    /**
     * @notice Pauses the factory
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @notice Unpauses the factory
     */
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @notice Grants orchestrator role to the orchestration service
     * @param orchestrator Address of the orchestration service operator
     */
    function grantOrchestratorRole(address orchestrator) external onlyRole(DEFAULT_ADMIN_ROLE) {
        grantRole(ORCHESTRATOR_ROLE, orchestrator);
    }
}

contracts/ethereum-hub/escrow/EscrowDst..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "../libraries/ImmutablesLib.sol";
import "../libraries/TimelocksLib.sol";

/**
 * @title EscrowDst
 * @notice Destination chain escrow for cross-chain atomic swaps
 * @dev Mirrors EscrowSrc behavior on destination chain
 */
contract EscrowDst is ReentrancyGuard {
    using SafeERC20 for IERC20;
    using ImmutablesLib for ImmutablesLib.Immutables;
    using TimelocksLib for TimelocksLib.Timelocks;

    // -- State --
    ImmutablesLib.Immutables private _immutables;
    uint256 private _srcCancellationTimestamp;
    bool private _initialized;
    bool private _withdrawn;
    bool private _cancelled;
    bytes32 private _revealedSecret;

    // -- Events --
    event EscrowInitialized(
        address indexed maker,
        address indexed taker,
        uint256 amount,
        bytes32 hashlockHash,
        uint256 srcCancellationTimestamp
    );

    event SecretRevealed(bytes32 indexed hashlockHash, bytes32 secret);
    event FundsWithdrawn(address indexed recipient, uint256 amount);
    event EscrowCancelled(address indexed maker);

    // -- Errors --
    error AlreadyInitialized();
    error NotInitialized();
    error AlreadyWithdrawn();
    error AlreadyCancelled();
    error InvalidSecret();
    error NotInWithdrawalWindow();
    error NotInCancellationWindow();
    error UnauthorizedCaller();
    error TransferFailed();
    error InvalidTimelockCoordination();

    // -- Modifiers --
    modifier onlyInitialized() {
        if (!_initialized) revert NotInitialized();
        _;
    }

    /**
     * @notice Initializes the escrow with immutable parameters
     * @dev Called by factory immediately after deployment
     * @param encodedImmutables Encoded immutable parameters
     * @param srcCancellationTimestamp Source chain cancellation timestamp for coordination
     */
    function initialize(
        bytes calldata encodedImmutables,
        uint256 srcCancellationTimestamp
    ) external payable {
        if (_initialized) revert AlreadyInitialized();

        _immutables = ImmutablesLib.decode(encodedImmutables);
        _srcCancellationTimestamp = srcCancellationTimestamp;

        // Validate cross-chain timeout coordination
        if (_immutables.timelocks.dstCancellation >= srcCancellationTimestamp) {
            revert InvalidTimelockCoordination();
        }

        _initialized = true;

        // Validate safety deposit
        require(msg.value == _immutables.safetyDeposit, "Invalid safety deposit");

        emit EscrowInitialized(
            _immutables.maker,
            _immutables.taker,
            _immutables.amount,
            _immutables.hashlockHash,
            srcCancellationTimestamp
        );
    }

    /**
     * @notice Withdraws funds by revealing the secret
     * @dev On destination chain, maker withdraws (roles are swapped)
     * @param secret The secret that hashes to hashlockHash
     */
    function withdraw(bytes32 secret) external nonReentrant onlyInitialized {
        if (_withdrawn) revert AlreadyWithdrawn();
        if (_cancelled) revert AlreadyCancelled();

        // Verify secret
        if (keccak256(abi.encodePacked(secret)) != _immutables.hashlockHash) {
            revert InvalidSecret();
        }

        // Check withdrawal window
        if (!_immutables.timelocks.canWithdraw(false, false)) {
            revert NotInWithdrawalWindow();
        }

        // On destination chain, the original maker (now taker) withdraws
        if (msg.sender != _immutables.taker) {
            revert UnauthorizedCaller();
        }

        // Mark as withdrawn and store secret
        _withdrawn = true;
        _revealedSecret = secret;

        emit SecretRevealed(_immutables.hashlockHash, secret);

        // Transfer tokens to taker (original maker)
        IERC20(_immutables.token).safeTransfer(_immutables.taker, _immutables.amount);

        // Return safety deposit
        (bool success,) = _immutables.taker.call{value: _immutables.safetyDeposit}("");
        if (!success) revert TransferFailed();

        emit FundsWithdrawn(_immutables.taker, _immutables.amount);
    }

    /**
     * @notice Cancels the escrow and refunds the maker
     * @dev On destination chain, the original taker (now maker) gets refund
     */
    function cancel() external nonReentrant onlyInitialized {
        if (_withdrawn) revert AlreadyWithdrawn();
        if (_cancelled) revert AlreadyCancelled();

        // Check if cancellation is allowed
        if (!_immutables.timelocks.canCancel(false)) {
            revert NotInCancellationWindow();
        }

        // On destination chain, the original taker (now maker) can cancel
        if (msg.sender != _immutables.maker) {
            revert UnauthorizedCaller();
        }

        // Mark as cancelled
        _cancelled = true;

        // Transfer tokens back to maker (original taker)
        IERC20(_immutables.token).safeTransfer(_immutables.maker, _immutables.amount);

        // Return safety deposit
        (bool success,) = _immutables.maker.call{value: _immutables.safetyDeposit}("");
        if (!success) revert TransferFailed();

        emit EscrowCancelled(_immutables.maker);
    }

    /**
     * @notice Gets the current escrow status
     * @return initialized Whether the escrow is initialized
     * @return withdrawn Whether funds have been withdrawn
     * @return cancelled Whether the escrow is cancelled
     */
    function getStatus() external view returns (
        bool initialized,
        bool withdrawn,
        bool cancelled,
        bool inWithdrawalWindow,
        bool inCancellationWindow,
        uint256 srcCancellationTime
    ) {
        initialized = _initialized;
        withdrawn = _withdrawn;
        cancelled = _cancelled;
        srcCancellationTime = _srcCancellationTimestamp;

        if (_initialized) {
            inWithdrawalWindow = _immutables.timelocks.canWithdraw(false, false);
            inCancellationWindow = _immutables.timelocks.canCancel(false);
        }
    }

    /**
     * @notice Gets the immutable parameters
     * @return Immutables structure
     */
    function getImmutables() external view onlyInitialized returns (ImmutablesLib.Immutables memory) {
        return _immutables;
    }

    /**
     * @notice Gets the revealed secret (if any)
     * @return secret The revealed secret
     */
    function getRevealedSecret() external view returns (bytes32) {
        return _revealedSecret;
    }

    /**
     * @notice Prepares tokens for cross-chain swap
     * @dev Called by maker to lock tokens in escrow
     */
    function lockTokens() external onlyInitialized {
        require(msg.sender == _immutables.maker, "Only maker can lock");
        require(!_withdrawn && !_cancelled, "Already finalized");

        // Transfer tokens from maker to escrow
        IERC20(_immutables.token).safeTransferFrom(
            msg.sender,
            address(this),
            _immutables.amount
        );
    }

    /**
     * @notice Emergency function to recover stuck tokens
     * @dev Can only be called by maker after cancellation window
     * @param token Token to recover
     * @param amount Amount to recover
     */
    function emergencyRecover(address token, uint256 amount) external onlyInitialized {
        require(msg.sender == _immutables.maker, "Only maker");
        require(_immutables.timelocks.canCancel(false), "Not in cancellation window");
        require(!_withdrawn && !_cancelled, "Already finalized");

        // Safety check: don't allow recovering the escrowed tokens normally
        if (token == _immutables.token) {
            require(amount <= IERC20(token).balanceOf(address(this)) - _immutables.amount, 
                "Cannot recover escrowed funds");
        }

        IERC20(token).safeTransfer(_immutables.maker, amount);
    }

    // Receive function to accept safety deposits
    receive() external payable {}
}

contracts/ethereum-hub/escrow/EscrowFact.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import "./BaseEscrowFactory.sol";
import "./EscrowSrc.sol";
import "./EscrowDst.sol";

/**
 * @title EscrowFactory
 * @notice Concrete implementation of escrow factory for 1inch integration
 * @dev Deploys and manages escrows for cross-chain atomic swaps
 */
contract EscrowFactory is BaseEscrowFactory {

    // -- Constructor --
    constructor(
        address _limitOrderProtocol,
        address _admin
    ) BaseEscrowFactory(
        address(new EscrowSrc()),      // Deploy src implementation
        address(new EscrowDst()),      // Deploy dst implementation
        _limitOrderProtocol
    ) {
        // Grant admin role to specified address (for orchestration service)
        if (_admin != msg.sender) {
            _grantRole(DEFAULT_ADMIN_ROLE, _admin);
            _grantRole(ORCHESTRATOR_ROLE, _admin);
        }
    }

    /**
     * @notice Creates escrows for orchestration service
     * @dev Batch creation for efficiency
     * @param srcImmutables Source chain immutables
     * @param dstImmutables Destination chain immutables
     * @return srcEscrow Source escrow address
     * @return dstEscrow Destination escrow address
     */
    function createEscrowPair(
        ImmutablesLib.Immutables calldata srcImmutables,
        ImmutablesLib.Immutables calldata dstImmutables
    ) external payable onlyRole(ORCHESTRATOR_ROLE) returns (
        address srcEscrow,
        address dstEscrow
    ) {
        // Calculate required safety deposits
        uint256 totalSafetyDeposit = srcImmutables.safetyDeposit + dstImmutables.safetyDeposit;
        require(msg.value == totalSafetyDeposit, "Invalid safety deposit total");

        // Create source escrow
        srcEscrow = this.createSrcEscrow{value: srcImmutables.safetyDeposit}(srcImmutables);

        // Create destination escrow
        uint256 srcCancellationTimestamp = srcImmutables.timelocks.srcCancellation;
        dstEscrow = this.createDstEscrow{value: dstImmutables.safetyDeposit}(
            dstImmutables,
            srcCancellationTimestamp
        );
    }

    /**
     * @notice Gets escrow addresses for a cross-chain order
     * @param orderHash The order hash
     * @return srcEscrow Source escrow address
     * @return dstEscrow Destination escrow address
     */
    function getEscrowPair(bytes32 orderHash) external view returns (
        address srcEscrow,
        address dstEscrow
    ) {
        // In production, this would query both chains
        // For hackathon, we simulate with computed addresses
        srcEscrow = escrows[orderHash];
        // Destination escrow would be on different chain
        dstEscrow = address(0); // Placeholder
    }

    /**
     * @notice Verifies escrow status for orchestration
     * @param escrowAddress Escrow to check
     * @return isValid True if escrow is valid and active
     */
    function verifyEscrowStatus(address escrowAddress) external view returns (bool isValid) {
        if (!isValidEscrow[escrowAddress]) {
            return false;
        }

        // Check escrow status
        try EscrowSrc(payable(escrowAddress)).getStatus() returns (
            bool initialized,
            bool withdrawn,
            bool cancelled,
            bool,
            bool
        ) {
            isValid = initialized && !withdrawn && !cancelled;
        } catch {
            // Try as destination escrow
            try EscrowDst(payable(escrowAddress)).getStatus() returns (
                bool initialized_,
                bool withdrawn_,
                bool cancelled_,
                bool,
                bool,
                uint256
            ) {
                isValid = initialized_ && !withdrawn_ && !cancelled_;
            } catch {
                isValid = false;
            }
        }
    }

    /**
     * @notice Emergency pause for all escrow creation
     * @dev Only admin can call during emergencies
     */
    function emergencyPause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @notice Resume escrow creation after emergency
     * @dev Only admin can call
     */
    function emergencyUnpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }
}

