pragma solidity ^0.8.23;

/// [Chainlink] Log Trigger Upkeeps + ILogAutomation:
/// https://docs.chain.link/chainlink-automation/guides/log-trigger

interface ILogAutomation {
    struct Log {
        uint256 index;
        uint256 timestamp;
        bytes32 txHash;
        uint256 blockNumber;
        bytes32 blockHash;
        address source;
        bytes32[] topics;
        bytes data;
    }

    function checkLog(Log calldata log, bytes calldata checkData) external returns (bool upkeepNeeded, bytes memory performData);
    function performUpkeep(bytes calldata performData) external;
}

import { IDiaPushOracleReceiver } from "../interfaces/IDiaPushOracleReceiver.sol";

interface IDriftRebalance {
    function triggerRebalance() external;
    function getStablecoins() external view returns (address[] memory);
    function getAssetAddresses() external view returns (address[] memory);
}

/// [DIA] Oracle V2 classic event (ABI ref):
/// event OracleUpdate(string key, uint128 value, uint128 timestamp)
/// Confirm Push Receiver’s actual event signature on Basescan and update topic0 if needed.
contract DriftBalancerLogger is ILogAutomation {
    address public immutable oracle; // DIA PushOracleReceiver
    address public immutable targetBalancer; // DriftBalancer to invoke
    // Thresholds per key (bps). keys hashed to bytes32 to keep storage cheap
    mapping(bytes32 => uint256) public driftThresholdBps; // keccak256(key) => threshold

    event DriftRebalanced(string key, uint128 value, uint128 ts);

    constructor(address _oracle, address _targetBalancer, string[] memory keys, uint256[] memory thresholdsBps) {
        require(_oracle != address(0), "oracle=0");
        require(_targetBalancer != address(0), "target=0");
        require(keys.length == thresholdsBps.length, "len mismatch");
        oracle = _oracle;
        targetBalancer = _targetBalancer;
        for (uint256 i = 0; i < keys.length; i++) {
            driftThresholdBps[keccak256(bytes(keys[i]))] = thresholdsBps[i];
        }
    }

    // topic0 must match the registered filter. For DIA classic:
    // bytes32(keccak256("OracleUpdate(string,uint128,uint128)"))
    // If Push Receiver differs, compute new topic0 off-chain and use that in registration.
    function checkLog(Log calldata log, bytes calldata) external returns (bool upkeepNeeded, bytes memory performData) {
        // Parse DIA payload
        (string memory key, uint128 value, uint128 ts) = abi.decode(log.data, (string, uint128, uint128));
        // Optionally scope by key thresholds
        uint256 thr = driftThresholdBps[keccak256(bytes(key))];
        if (thr == 0) {
            // If not configured, still allow upkeep to let target balancer decide
            performData = abi.encode(key, value, ts);
            return (true, performData);
        }
        // Lightweight precheck: only stablecoin keys trigger immediately
        // Full portfolio checks happen in performUpkeep via targetBalancer.triggerRebalance()
        performData = abi.encode(key, value, ts);
        return (true, performData);
    }

    function performUpkeep(bytes calldata performData) external override {
        (string memory key, uint128 value, uint128 ts) = abi.decode(performData, (string, uint128, uint128));
        // Optional: read latest on-chain pushed value to confirm
        // (uint128 lastTs, uint128 lastVal) = IDiaPushOracleReceiver(oracle).updates(key);

        // Forward to portfolio DriftBalancer to apply library-based analysis and emit orders/events
        // This connects Automation Log trigger with existing portfolio logic.
        try IDriftRebalance(targetBalancer).triggerRebalance() {
            // no-op
        } catch {}

        emit DriftRebalanced(key, value, ts);
    }
}

","keccak256":"0x9370ca49b8610b139bbcef03861ed60355e73f84c143de87d57aac14a38d73ea","license":"MIT"},"contracts/portfolio/balancers/BaseBalancer.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@chainlink/contracts/src/v0.8/automation/interfaces/AutomationCompatibleInterface.sol";

import "../interfaces/IBalancerFactory.sol";
import "../interfaces/IOracleAdapter.sol";
import "../interfaces/IERC1271.sol";
import "../interfaces/ILimitOrderProtocol.sol";
import "../libraries/LimitOrderLib.sol";
import "../libraries/PortfolioAnalysisLib.sol";

abstract contract BaseBalancer is Ownable, Pausable, ReentrancyGuard, IERC1271, AutomationCompatibleInterface {
    using ECDSA for bytes32;
    using SafeERC20 for IERC20;
    struct AssetGroup {
        uint256 percentage;
        address[] tokens;
        bool isStablecoinGroup;
    }

    mapping(uint256 => AssetGroup) public assetGroups;
    uint256 public assetGroupsCount;
    address[] public assetAddresses;
    uint256 public lastUpdateTimestamp;

    IBalancerFactory public factory;
    address[] public stablecoins;
    mapping(address => bool) public isStablecoin;

    // Signature auth and automation forwarder
    address public authorizedSigner;
    address public s_forwarderAddress;

    // Orders / protocol
    ILimitOrderProtocol public limitOrderProtocol;
    bytes32 public domainSeparator;
    uint256 internal nonce;

    // Metadata
    string public name;
    string public description;

    event StablecoinsUpdated(address[] newStablecoins);
    event ForwarderUpdated(address forwarder);


    constructor(
        address _owner,
        address _factory,
        address[] memory _assetAddresses,
        uint256[] memory _percentages,
        address[] memory _stablecoins,
        address _limitOrderProtocol,
        string memory _name,
        string memory _description
    ) Ownable(_owner) {
        factory = IBalancerFactory(_factory);

        for (uint256 i = 0; i < _stablecoins.length; i++) {
            stablecoins.push(_stablecoins[i]);
            isStablecoin[_stablecoins[i]] = true;
        }

        _updateAssetGroupMapping(_assetAddresses, _percentages, _stablecoins);
        lastUpdateTimestamp = block.timestamp;

        authorizedSigner = _owner;
        // Default forwarder to owner to enable local testing and safe manual calls
        s_forwarderAddress = _owner;
        name = _name;
        description = _description;

        // Limit order protocol and domain separator
        limitOrderProtocol = ILimitOrderProtocol(_limitOrderProtocol);
        domainSeparator = keccak256(
            abi.encode(
                keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
                keccak256(bytes("1inch Limit Order Protocol")),
                keccak256(bytes("4")),
                block.chainid,
                _limitOrderProtocol
            )
        );
    }

    function updateStablecoins(address[] memory _stablecoins) external onlyOwner {
        for (uint256 i = 0; i < stablecoins.length; i++) {
            isStablecoin[stablecoins[i]] = false;
        }
        delete stablecoins;
        for (uint256 i2 = 0; i2 < _stablecoins.length; i2++) {
            stablecoins.push(_stablecoins[i2]);
            isStablecoin[_stablecoins[i2]] = true;
        }
        emit StablecoinsUpdated(_stablecoins);
    }

    function updateAssetMapping(
        address[] memory _assetAddresses,
        uint256[] memory _percentages,
        address[] memory _stablecoins
    ) external onlyOwner {
        _updateAssetGroupMapping(_assetAddresses, _percentages, _stablecoins);
    }



    // ===== Authorization controls =====
    function updateAuthorizedSigner(address newSigner) external onlyOwner {
        authorizedSigner = newSigner;
    }

    function updateMetadata(string calldata _name, string calldata _description) external onlyOwner {
        name = _name;
        description = _description;
    }

    function pause() external onlyOwner { _pause(); }
    function unpause() external onlyOwner { _unpause(); }

    // ===== Automation-compatible API (common gating) =====
    function checkUpkeep(bytes calldata checkData) external view virtual override returns (bool upkeepNeeded, bytes memory performData) {
        return _checkUpkeep(checkData);
    }

    function performUpkeep(bytes calldata performData) external virtual override whenNotPaused {
        require(msg.sender == s_forwarderAddress, "Not authorized forwarder");
        _performUpkeep(performData);
    }

    function setForwarderAddress(address forwarder) external onlyOwner {
        s_forwarderAddress = forwarder;
        emit ForwarderUpdated(forwarder);
    }

    // Strategy hooks to implement per balancer
    function _checkUpkeep(bytes calldata checkData) internal view virtual returns (bool, bytes memory);
    function _performUpkeep(bytes calldata performData) internal virtual;

    // ===== Funding =====
    function fund(address _asset, uint256 _amount) external onlyOwner {
        require(_isValidAsset(_asset), "Asset not found in any group");
        IERC20(_asset).safeTransferFrom(msg.sender, address(this), _amount);
    }
    function withdraw(address _asset, uint256 _amount) external onlyOwner nonReentrant {
        require(_isValidAsset(_asset), "Asset not found in any group");
        IERC20(_asset).safeTransfer(msg.sender, _amount);
    }

    function getTotalValue() public view returns (uint256) {
        uint256 totalValue = 0;
        for (uint256 i = 0; i < assetAddresses.length; i++) {
            address asset = assetAddresses[i];
            uint256 balance = IERC20(asset).balanceOf(address(this));
            totalValue += getPrice(asset, balance);
        }
        return totalValue;
    }

    function getPrice(address asset, uint256 amount) public view returns (uint256) {
        address priceFeedAddr = factory.priceFeed();
        uint256 price = IOracleAdapter(priceFeedAddr).getRateToEth(asset, false);
        return (amount * price) / 1e18;
    }

    function getPortfolioAnalysis() external view returns (uint256 portfolioValue, uint256 stablecoinRatio, bool isBalanced) {
        portfolioValue = getTotalValue();
        if (portfolioValue == 0) return (0, 0, true);
        uint256 totalStablecoinValue = _getTotalStablecoinValue();
        stablecoinRatio = PortfolioAnalysisLib.calculatePortfolioMetrics(portfolioValue, totalStablecoinValue);
        isBalanced = _checkIfBalanced(portfolioValue);
    }

    // ===== EIP-1271 =====
    bytes4 private constant MAGIC_VALUE = 0x1626ba7e;

    function isValidSignature(bytes32 _hash, bytes memory _signature) external view override returns (bytes4 magicValue) {
        // Try EIP-191 personal_sign digest first
        bytes32 ethDigest = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", _hash));
        (address rec1, ECDSA.RecoverError err1, ) = ECDSA.tryRecover(ethDigest, _signature);
        if (err1 == ECDSA.RecoverError.NoError && (rec1 == authorizedSigner || rec1 == owner())) {
            return MAGIC_VALUE;
        }
        // Fallback to raw hash (e.g., EIP-712 already-digested input)
        (address rec2, ECDSA.RecoverError err2, ) = ECDSA.tryRecover(_hash, _signature);
        if (err2 == ECDSA.RecoverError.NoError && (rec2 == authorizedSigner || rec2 == owner())) {
            return MAGIC_VALUE;
        }
        return 0xffffffff;
    }

    // Helper: contract cannot produce ECDSA signatures on-chain, keep for ABI compatibility
    function getOrderSignature(bytes32) external pure returns (bytes memory signature) {
        return bytes("");
    }

    // ===== Orders (shared) =====
    event LimitOrderCreated(
        bytes32 indexed orderHash,
        address indexed maker,
        address sellToken,
        address buyToken,
        uint256 sellAmount,
        uint256 buyAmount
    );
    event RebalanceOrderCreated(
        bytes32 indexed orderHash,
        address indexed maker,
        address sellToken,
        address buyToken,
        uint256 sellAmount,
        uint256 buyAmount,
        uint256 slippageTolerance
    );

    function createRebalanceOrder(
        address sellToken,
        address buyToken,
        uint256 sellAmount,
        uint256 buyAmount,
        uint256 slippageTolerance
    ) public onlyOwner returns (bytes32 orderHash) {
        LimitOrderLib.RebalanceOrder memory rebalanceOrder = LimitOrderLib.RebalanceOrder({
            sellToken: sellToken,
            buyToken: buyToken,
            sellAmount: sellAmount,
            buyAmount: buyAmount,
            slippageTolerance: slippageTolerance
        });
        ILimitOrderProtocol.Order memory order = LimitOrderLib.createRebalanceOrder(
            rebalanceOrder,
            address(this),
            nonce++,
            block.timestamp + 3600
        );
        orderHash = LimitOrderLib.calculateOrderHash(order, domainSeparator);
        emit RebalanceOrderCreated(orderHash, address(this), sellToken, buyToken, sellAmount, buyAmount, slippageTolerance);
    }

    function createStablecoinGridOrder(
        address fromToken,
        address toToken,
        uint256 amount,
        uint256 limitPrice
    ) public onlyOwner returns (bytes32 orderHash) {
        ILimitOrderProtocol.Order memory order = LimitOrderLib.createLimitOrder(
            address(this),
            address(this),
            fromToken,
            toToken,
            amount,
            (amount * limitPrice) / 1e18,
            nonce++,
            true,
            false,
            block.timestamp + 3600
        );
        orderHash = LimitOrderLib.calculateOrderHash(order, domainSeparator);
        emit LimitOrderCreated(orderHash, address(this), fromToken, toToken, amount, (amount * limitPrice) / 1e18);
    }

    // ===== Portfolio helpers shared =====
    function _getTotalStablecoinValue() internal view returns (uint256) {
        uint256 totalValue = 0;
        for (uint256 i = 0; i < stablecoins.length; i++) {
            address stablecoin = stablecoins[i];
            uint256 balance = IERC20(stablecoin).balanceOf(address(this));
            if (balance > 0) {
                uint256 price = IOracleAdapter(factory.priceFeed()).getRateToEth(stablecoin, false);
                totalValue += (balance * price) / 1e18;
            }
        }
        return totalValue;
    }

    function _checkIfBalanced(uint256 portfolioValue) internal view returns (bool) {
        for (uint256 groupId = 0; groupId < assetGroupsCount; groupId++) {
            AssetGroup memory group = assetGroups[groupId];
            uint256 groupValue = 0;
            for (uint256 i = 0; i < group.tokens.length; i++) {
                uint256 balance = IERC20(group.tokens[i]).balanceOf(address(this));
                groupValue += getPrice(group.tokens[i], balance);
            }
            uint256 currentPercentage = (groupValue * 100) / portfolioValue;
            uint256 targetPercentage = group.percentage;
            (bool withinRange, ) = PortfolioAnalysisLib.checkAssetBalance(currentPercentage, targetPercentage);
            if (!withinRange) return false;
        }
        return true;
    }

    function getAssetAddresses() external view returns (address[] memory) {
        return assetAddresses;
    }

    function getStablecoins() external view returns (address[] memory) {
        return stablecoins;
    }

    function getAssetGroup(uint256 groupId) external view returns (AssetGroup memory) {
        return assetGroups[groupId];
    }

    function getAssetBalance(address _asset) external view returns (uint256) {
        return IERC20(_asset).balanceOf(address(this));
    }

    function _isValidAsset(address _asset) internal view returns (bool) {
        for (uint256 i = 0; i < assetAddresses.length; i++) {
            if (assetAddresses[i] == _asset) return true;
        }
        return false;
    }

    function _updateAssetGroupMapping(
        address[] memory _assetAddresses,
        uint256[] memory _percentages,
        address[] memory _stablecoins
    ) internal {
        require(_percentages.length > 0, "No percentages provided");
        require(_assetAddresses.length > 0, "No assets provided");

        uint256 nonStablecoinCount = 0;
        uint256 stablecoinCount = 0;
        for (uint256 i = 0; i < _assetAddresses.length; i++) {
            bool isStable = false;
            for (uint256 j = 0; j < _stablecoins.length; j++) {
                if (_assetAddresses[i] == _stablecoins[j]) {
                    isStable = true;
                    stablecoinCount++;
                    break;
                }
            }
            if (!isStable) {
                nonStablecoinCount++;
            }
        }
        uint256 expectedPercentageCount = (stablecoinCount > 0 ? 1 : 0) + nonStablecoinCount;
        require(_percentages.length == expectedPercentageCount, "Wrong percentage count for asset groups");

        for (uint256 i2 = 0; i2 < assetGroupsCount; i2++) {
            delete assetGroups[i2];
        }
        assetAddresses = _assetAddresses;
        assetGroupsCount = 0;

        if (stablecoinCount > 0) {
            address[] memory stablecoinAssets = new address[](stablecoinCount);
            uint256 stablecoinIndex = 0;
            for (uint256 i3 = 0; i3 < _assetAddresses.length; i3++) {
                for (uint256 j2 = 0; j2 < _stablecoins.length; j2++) {
                    if (_assetAddresses[i3] == _stablecoins[j2]) {
                        stablecoinAssets[stablecoinIndex] = _assetAddresses[i3];
                        stablecoinIndex++;
                        break;
                    }
                }
            }
            assetGroups[assetGroupsCount] = AssetGroup({ percentage: _percentages[0], tokens: stablecoinAssets, isStablecoinGroup: true });
            assetGroupsCount++;
        }

        uint256 percentageIndex = stablecoinCount > 0 ? 1 : 0;
        for (uint256 i4 = 0; i4 < _assetAddresses.length; i4++) {
            bool isStable2 = false;
            for (uint256 j3 = 0; j3 < _stablecoins.length; j3++) {
                if (_assetAddresses[i4] == _stablecoins[j3]) {
                    isStable2 = true;
                    break;
                }
            }
            if (!isStable2) {
                address[] memory singleAsset = new address[](1);
                singleAsset[0] = _assetAddresses[i4];
                assetGroups[assetGroupsCount] = AssetGroup({ percentage: _percentages[percentageIndex], tokens: singleAsset, isStablecoinGroup: false });
                assetGroupsCount++;
                percentageIndex++;
            }
        }
    }
}

","keccak256":"0x1d98e6a133174ddea1d8f8d703f3faf0012872618f94e1b9bbf43a4d8feb8ff7","license":"MIT"},"contracts/portfolio/balancers/DriftBalancer.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../interfaces/IOracleAdapter.sol";
import "../interfaces/IBalancerFactory.sol";

import "../libraries/StablecoinGridLib.sol";
import "../libraries/PortfolioAnalysisLib.sol";
import "../libraries/StablecoinAnalysisLib.sol";
import "./BaseBalancer.sol";

contract DriftBalancer is BaseBalancer {
    using SafeERC20 for IERC20;

    // ===== Metadata / Config =====
    uint256 public driftPercentage;
    // name/description inherited in base


    // ===== Events =====
    event DriftPercentageUpdated(uint256 newDriftPercentage);
    event RebalanceNeeded(address[] tokens, uint256[] deviations);
    event OrdersGenerated(StablecoinGridLib.Order[] orders);

    constructor(
        address _owner,
        address _factory,
        address[] memory _assetAddresses,
        uint256[] memory _percentages,
        uint256 _driftPercentage,
        address[] memory _stablecoins,
        address _limitOrderProtocol,
        string memory _name,
        string memory _description
    ) BaseBalancer(_owner, _factory, _assetAddresses, _percentages, _stablecoins, _limitOrderProtocol, _name, _description) {
        driftPercentage = _driftPercentage;
    }

    // ===== Owner controls =====
    function updateDriftPercentage(uint256 _newDriftPercentage) external onlyOwner {
        driftPercentage = _newDriftPercentage;
        emit DriftPercentageUpdated(_newDriftPercentage);
    }

    // ===== Automation hooks =====
    function _checkUpkeep(bytes calldata /*checkData*/) internal view override returns (bool upkeepNeeded, bytes memory performData) {
        // Require ~0.1% drift (1e15 in 18 decimals) to trigger upkeep
        return StablecoinAnalysisLib.detectDeviation(stablecoins, factory.priceFeed(), 1e15);
    }

    function _performUpkeep(bytes calldata /*performData*/) internal override {
        uint256 stablecoinValue = StablecoinAnalysisLib.totalStablecoinValue(stablecoins, factory.priceFeed(), address(this));
        StablecoinGridLib.GridParams memory params = StablecoinGridLib.calculateGridParams(
            stablecoinValue,
            5,
            15
        );
        StablecoinGridLib.Order[] memory orders = StablecoinGridLib.generateGridOrders(stablecoins, params);
        emit OrdersGenerated(orders);

        for (uint256 i = 0; i < orders.length; i++) {
            StablecoinGridLib.Order memory gridOrder = orders[i];
            bytes32 orderHash = createStablecoinGridOrder(
                gridOrder.fromToken,
                gridOrder.toToken,
                gridOrder.amount,
                gridOrder.limitPrice
            );
            emit LimitOrderCreated(orderHash, address(this), gridOrder.fromToken, gridOrder.toToken, gridOrder.amount, gridOrder.limitPrice);
        }
    }

    // ===== Rebalance trigger =====
    function triggerRebalance() external { _checkAndTriggerRebalance(); }

    function _checkAndTriggerRebalance() internal {
        uint256 totalValue = getTotalValue();
        if (totalValue == 0) return;

        uint256[] memory groupDeviations = new uint256[](assetGroupsCount);
        address[][] memory groupTokens = new address[][](assetGroupsCount);
        bool rebalanceNeeded = false;

        for (uint256 groupId = 0; groupId < assetGroupsCount; groupId++) {
            AssetGroup memory group = assetGroups[groupId];
            uint256 groupValue = 0;
            for (uint256 i = 0; i < group.tokens.length; i++) {
                uint256 balance = IERC20(group.tokens[i]).balanceOf(address(this));
                groupValue += getPrice(group.tokens[i], balance);
            }
            uint256 currentPercentage = (groupValue * 100) / totalValue;
            uint256 targetPercentage = group.percentage;
            uint256 deviation = currentPercentage > targetPercentage ? currentPercentage - targetPercentage : targetPercentage - currentPercentage;
            groupDeviations[groupId] = deviation;
            groupTokens[groupId] = group.tokens;
            if (deviation > driftPercentage) {
                rebalanceNeeded = true;
            }
            if (group.isStablecoinGroup && group.tokens.length > 1) {
                address referenceStable = stablecoins.length > 0 ? stablecoins[0] : address(0);
                (bool need, uint256 devBps) = StablecoinAnalysisLib.computeGroupStablecoinDeviation(
                    group.tokens,
                    referenceStable,
                    factory.priceFeed(),
                    1e15
                );
                if (need) {
                    rebalanceNeeded = true;
                    groupDeviations[groupId] = devBps;
                }
            }
        }

        if (rebalanceNeeded) {
            address[] memory allTokens = new address[](assetAddresses.length);
            uint256[] memory allDeviations = new uint256[](assetAddresses.length);
            uint256 tokenIndex = 0;
            for (uint256 groupId2 = 0; groupId2 < assetGroupsCount; groupId2++) {
                for (uint256 i3 = 0; i3 < groupTokens[groupId2].length; i3++) {
                    allTokens[tokenIndex] = groupTokens[groupId2][i3];
                    allDeviations[tokenIndex] = groupDeviations[groupId2];
                    tokenIndex++;
                }
            }
            emit RebalanceNeeded(allTokens, allDeviations);
        }
    }

    // All order, EIP-1271, and portfolio helpers are inherited from base
    receive() external payable {}
}
","keccak256":"0x3d0aedca15b70655164c6df172dad660943424a649de2c8623f691ad3d7027e2","license":"MIT"},"contracts/portfolio/balancers/TimeBalancer.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import "../interfaces/IBalancerFactory.sol";
import "../interfaces/IOracleAdapter.sol";
import "../libraries/StablecoinAnalysisLib.sol";
import "./BaseBalancer.sol";

contract TimeBalancer is BaseBalancer {
    uint256 public interval;
    uint256 public lastRebalance;
    string public timeName;
    string public timeDescription;

    event IntervalUpdated(uint256 newInterval);
    event RebalanceNeeded(uint256[] allocations, uint256 timestamp);

    constructor(
        address _owner,
        address _factory,
        address[] memory _assetAddresses,
        uint256[] memory _percentages,
        uint256 _interval,
        address[] memory _stablecoins,
        address _limitOrderProtocol,
        string memory _name,
        string memory _description
    ) BaseBalancer(_owner, _factory, _assetAddresses, _percentages, _stablecoins, _limitOrderProtocol, _name, _description) {
        interval = _interval;
        lastRebalance = block.timestamp;
        timeName = _name;
        timeDescription = _description;
    }

    function triggerTimeRebalance() external {
        require(block.timestamp >= lastRebalance + interval, "Too early");
        lastRebalance = block.timestamp;

        // Also detect stablecoin deviations using shared library
        (bool upkeepNeeded, ) = StablecoinAnalysisLib.detectDeviation(stablecoins, IBalancerFactory(factory).priceFeed(), 1e15);
        if (upkeepNeeded) {
            uint256[] memory current = currentAllocations();
            emit RebalanceNeeded(current, block.timestamp);
        }
    }

    // ===== Automation hooks =====
    function _checkUpkeep(bytes calldata /*checkData*/) internal view override returns (bool upkeepNeeded, bytes memory performData) {
        // Time-based: upkeep when interval elapsed
        bool due = block.timestamp >= lastRebalance + interval;
        if (!due) return (false, bytes(""));
        return (true, bytes(""));
    }

    function _performUpkeep(bytes calldata /*performData*/) internal override {
        // Simply emit that a rebalance is needed because interval elapsed
        uint256[] memory current = currentAllocations();
        emit RebalanceNeeded(current, block.timestamp);
        lastRebalance = block.timestamp;
    }

    function setRebalanceInterval(uint256 _interval) external onlyOwner {
        interval = _interval;
        emit IntervalUpdated(_interval);
    }

    function updateTimeMetadata(string calldata _name, string calldata _description) external onlyOwner {
        timeName = _name;
        timeDescription = _description;
    }

    function currentAllocations() public view returns (uint256[] memory) {
        uint256[] memory allocations = new uint256[](assetGroupsCount);
        for (uint256 i = 0; i < assetGroupsCount; i++) {
            allocations[i] = assetGroups[i].percentage;
        }
        return allocations;
    }
}
","keccak256":"0xeee588f83ec0bbd1074a3f4fd26317979b39ac7234d582b8836e7e5b8e936572","license":"MIT"},"contracts/portfolio/factory/BalancerFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

/*
 * OptimizedBalancerFactory
 *
 * Optimized factory contract that deploys OptimizedDriftBalancer or OptimizedTimeBalancer
 * instances with reduced contract sizes through library usage.
 */

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "../balancers/DriftBalancer.sol";
import "../balancers/TimeBalancer.sol";
import "../interfaces/ILimitOrderProtocol.sol";
import "@chainlink/contracts/src/v0.8/shared/interfaces/LinkTokenInterface.sol";
import "../automation/DriftBalancerLogger.sol";

// ===== Programmatic Upkeep Registration (file-level types) =====
// Minimal interfaces based on Chainlink docs
// https://docs.chain.link/chainlink-automation/guides/register-upkeep-in-contract
struct RegistrationParams {
    string name;
    bytes encryptedEmail;
    address upkeepContract;
    uint32 gasLimit;
    address adminAddress;
    uint8 triggerType; // 0 = conditional, 1 = log
    bytes checkData; // forwarded to checkUpkeep
    bytes triggerConfig; // 0x for conditional
    bytes offchainConfig; // optional CBOR config
    uint96 amount; // LINK amount (in wei) to fund at registration
}

interface IAutomationRegistrar {
    function registerUpkeep(RegistrationParams calldata requestParams) external returns (uint256);
}

// Minimal registry interface for reading the Forwarder
interface IAutomationRegistryMinimal {
    function getForwarder(uint256 upkeepId) external view returns (address);
}

contract BalancerFactory is Ownable {
    address public priceFeed;
    address[] public stablecoins;
    ILimitOrderProtocol public limitOrderProtocol;

    // ===== Chainlink Automation (programmatic registration) =====
    LinkTokenInterface public linkToken;
    address public automationRegistrar; // Automation Registrar (v2.1)
    address public automationRegistry; // Automation Registry (v2.1)

    // balancer => upkeepId (if registered via this factory)
    mapping(address => uint256) public balancerToUpkeepId;

    constructor(address _priceFeed, address[] memory _stablecoins, address _limitOrderProtocol) Ownable(msg.sender) {
        priceFeed = _priceFeed;
        stablecoins = _stablecoins;
        limitOrderProtocol = ILimitOrderProtocol(_limitOrderProtocol);
    }

    /// @dev Lists of deployed drift and time balancers per user
    mapping(address => address[]) public userDriftBalancers;
    mapping(address => address[]) public userTimeBalancers;

    /// @dev Emitted when a new balancer is created
    event BalancerCreated(address indexed owner, address indexed balancer, bool isTimeBased);
    event PriceFeedUpdated(address priceFeed);
    event StablecoinsSet(address[] stablecoins);
    event LimitOrderProtocolUpdated(address limitOrderProtocol);
    event AutomationAddressesSet(address linkToken, address registrar, address registry);
    event UpkeepRegistered(address indexed balancer, uint256 indexed upkeepId, address forwarder);
    event DriftLoggerDeployed(address indexed logger, address indexed targetBalancer);

    error NoStablecoin();

    /**
     * @notice Create a new OptimizedDriftBalancer
     */
    function createDriftBalancer(
        address[] memory _assetAddresses,
        uint256[] memory _percentages,
        uint256[] memory _amounts,
        uint256 _driftPercentage,
        string memory _name,
        string memory _description
    ) external returns (address balancer) {
        _requireAtLeastOneStablecoin(_assetAddresses);
        _checkUserTokenBalance(_assetAddresses, _amounts);

         balancer = address(new DriftBalancer(
            msg.sender, 
            address(this), 
            _assetAddresses, 
            _percentages, 
            _driftPercentage, 
            stablecoins,
            address(limitOrderProtocol),
            _name,
            _description
        ));

        _sendTokensToBalancer(balancer, _assetAddresses, _amounts);
        userDriftBalancers[msg.sender].push(balancer);
        emit BalancerCreated(msg.sender, balancer, false);
    }

    /**
     * @notice Create a new OptimizedTimeBalancer
     */
    function createTimeBalancer(
        address[] memory _assetAddresses,
        uint256[] memory _percentages,
        uint256[] memory _amounts,
        uint256 interval,
        string memory _name,
        string memory _description
    ) external returns (address balancer) {
        _requireAtLeastOneStablecoin(_assetAddresses);
        _checkUserTokenBalance(_assetAddresses, _amounts);

         balancer = address(new TimeBalancer(
            msg.sender, 
            address(this), 
            _assetAddresses, 
            _percentages, 
            interval, 
            stablecoins,
            address(limitOrderProtocol),
            _name,
            _description
        ));

        _sendTokensToBalancer(balancer, _assetAddresses, _amounts);
        userTimeBalancers[msg.sender].push(balancer);
        emit BalancerCreated(msg.sender, balancer, true);
    }

    /**
     * @notice Internal function to send tokens to a newly created balancer
     */
    function _sendTokensToBalancer(address balancer, address[] memory tokens, uint256[] memory amounts) internal {
        require(tokens.length == amounts.length, "Tokens and amounts length mismatch");
        for (uint256 i = 0; i < tokens.length; i++) {
            IERC20(tokens[i]).transferFrom(msg.sender, balancer, amounts[i]);
        }
    }

    /**
     * @notice Internal function to check if the asset addresses contain at least one stablecoin
     * @dev This function is not used in the current implementation as the stablecoins adressess will be hardcoded in the factory
     */
    function _requireAtLeastOneStablecoin(address[] memory _assetAddresses) internal view {
        for (uint i = 0; i < _assetAddresses.length; i++) {
            for (uint j = 0; j < stablecoins.length; j++) {
                if (_assetAddresses[i] == stablecoins[j]) {
                    return;
                }
            }
        }
        revert NoStablecoin();
    }

    function _checkUserTokenBalance(address[] memory tokens, uint256[] memory amounts) internal view {
        require(tokens.length == amounts.length, "Tokens and amounts length mismatch");
        for (uint256 i = 0; i < tokens.length; i++) {
            require(IERC20(tokens[i]).balanceOf(msg.sender) >= amounts[i], "Insufficient token balance in factory");
        }
    }

    function setPriceFeed(address _priceFeed) external onlyOwner {
        priceFeed = _priceFeed;
        emit PriceFeedUpdated(_priceFeed);
    }

    function setStablecoins(address[] calldata _stablecoins) external onlyOwner {
        stablecoins = _stablecoins;
        emit StablecoinsSet(_stablecoins);
    }

    function setLimitOrderProtocol(address _limitOrderProtocol) external onlyOwner {
        limitOrderProtocol = ILimitOrderProtocol(_limitOrderProtocol);
        emit LimitOrderProtocolUpdated(_limitOrderProtocol);
    }

    // Note: public dynamic array `stablecoins` already exposes `stablecoins(uint256)` getter

    // ===== Programmatic Upkeep Registration =====

    function setAutomationAddresses(address _linkToken, address _registrar, address _registry) external onlyOwner {
        linkToken = LinkTokenInterface(_linkToken);
        automationRegistrar = _registrar;
        automationRegistry = _registry;
        emit AutomationAddressesSet(_linkToken, _registrar, _registry);
    }

    /// @notice Register a log-trigger upkeep for a DriftBalancerLogger (DIA push oracle logs)
    /// @param balancer The deployed DriftBalancerLogger address
    /// @param gasLimit max gas for performUpkeep
    /// @param amountLinkWei initial LINK funding (wei)
    /// @param emittingContract DIA PushOracleReceiver contract emitting the event
    /// @param topic0 keccak256(eventSignature). For DIA classic: keccak256("OracleUpdate(string,uint128,uint128)")
    /// @param filterSelector per Chainlink docs; 0 = no indexed filters
    /// @param topic1 indexed topic if filtering by key; otherwise 0x
    /// @param topic2 unused
    /// @param topic3 unused
    function registerLogTriggerUpkeep(
        address balancer,
        uint32 gasLimit,
        uint96 amountLinkWei,
        address emittingContract,
        bytes32 topic0,
        uint8 filterSelector,
        bytes32 topic1,
        bytes32 topic2,
        bytes32 topic3
    ) external onlyOwner returns (uint256 upkeepId) {
        require(balancer != address(0), "balancer=0");
        require(address(linkToken) != address(0) && automationRegistrar != address(0), "Automation not set");

        linkToken.approve(automationRegistrar, amountLinkWei);

        bytes memory trigger = abi.encode(emittingContract, filterSelector, topic0, topic1, topic2, topic3);
        RegistrationParams memory params = RegistrationParams({
            name: string(abi.encodePacked("1Balancer-Log-", _shortAddr(balancer))),
            encryptedEmail: bytes(""),
            upkeepContract: balancer,
            gasLimit: gasLimit,
            adminAddress: owner(),
            triggerType: 1, // log trigger
            checkData: bytes(""),
            triggerConfig: trigger,
            offchainConfig: bytes(""),
            amount: amountLinkWei
        });

        upkeepId = IAutomationRegistrar(automationRegistrar).registerUpkeep(params);
        require(upkeepId != 0, "Registrar returned 0");
        balancerToUpkeepId[balancer] = upkeepId;

        address forwarder = address(0);
        if (automationRegistry != address(0)) {
            try IAutomationRegistryMinimal(automationRegistry).getForwarder(upkeepId) returns (address fwd) {
                forwarder = fwd;
            } catch {}
        }
        emit UpkeepRegistered(balancer, upkeepId, forwarder);
    }

    /// @notice Programmatically register a custom-logic upkeep for a deployed balancer
    /// @dev Approves LINK to registrar and calls registerUpkeep. Optionally sets forwarder if registry is provided.
    function registerBalancerUpkeep(
        address balancer,
        uint32 gasLimit,
        uint96 amountLinkWei,
        bytes calldata checkData
    ) external onlyOwner returns (uint256 upkeepId) {
        require(balancer != address(0), "balancer=0");
        require(address(linkToken) != address(0) && automationRegistrar != address(0), "Automation not set");

        // 1) Approve LINK to the registrar
        linkToken.approve(automationRegistrar, amountLinkWei);

        // 2) Build params and register
        RegistrationParams memory params = RegistrationParams({
            name: string(abi.encodePacked("1Balancer-", _shortAddr(balancer))),
            encryptedEmail: bytes("") /* 0x */, 
            upkeepContract: balancer,
            gasLimit: gasLimit,
            adminAddress: owner(),
            triggerType: 0, // 0=conditional (TimeBalancer). For DriftBalancerLogger use 1 and pass log trigger config.
            checkData: checkData,
            triggerConfig: bytes("") /* 0x */, 
            offchainConfig: bytes("") /* 0x */, 
            amount: amountLinkWei
        });

        upkeepId = IAutomationRegistrar(automationRegistrar).registerUpkeep(params);
        require(upkeepId != 0, "Registrar returned 0");
        balancerToUpkeepId[balancer] = upkeepId;

        // 3) If registry is set, fetch forwarder and set it on the balancer
        address forwarder = address(0);
        if (automationRegistry != address(0)) {
            try IAutomationRegistryMinimal(automationRegistry).getForwarder(upkeepId) returns (address fwd) {
                forwarder = fwd;
                try DriftBalancer(payable(balancer)).setForwarderAddress(forwarder) {} catch {}
            } catch {}
        }

        emit UpkeepRegistered(balancer, upkeepId, forwarder);
    }

    /// @notice Refresh and set the forwarder on a balancer from a known upkeepId
    function refreshBalancerForwarder(address balancer) external {
        uint256 upkeepId = balancerToUpkeepId[balancer];
        require(upkeepId != 0 && automationRegistry != address(0), "No upkeep or registry");
        address forwarder = IAutomationRegistryMinimal(automationRegistry).getForwarder(upkeepId);
        DriftBalancer(payable(balancer)).setForwarderAddress(forwarder);
        emit UpkeepRegistered(balancer, upkeepId, forwarder);
    }

    // ===== DIA Log-Trigger Drift Logger Deployment =====
    function createDriftBalancerLogger(
        address diaPushOracle,
        address targetBalancer,
        string[] memory keys,
        uint256[] memory thresholdsBps
    ) external onlyOwner returns (address logger) {
        logger = address(new DriftBalancerLogger(diaPushOracle, targetBalancer, keys, thresholdsBps));
        emit DriftLoggerDeployed(logger, targetBalancer);
    }

    function createAndRegisterDriftBalancerLogger(
        address diaPushOracle,
        address targetBalancer,
        string[] memory keys,
        uint256[] memory thresholdsBps,
        // registration params
        uint32 gasLimit,
        uint96 amountLinkWei,
        address emittingContract,
        bytes32 topic0,
        uint8 filterSelector,
        bytes32 topic1,
        bytes32 topic2,
        bytes32 topic3
    ) external onlyOwner returns (address logger, uint256 upkeepId) {
        logger = address(new DriftBalancerLogger(diaPushOracle, targetBalancer, keys, thresholdsBps));
        emit DriftLoggerDeployed(logger, targetBalancer);

        // Register log trigger upkeep for the newly deployed logger
        upkeepId = this.registerLogTriggerUpkeep(
            logger,
            gasLimit,
            amountLinkWei,
            emittingContract,
            topic0,
            filterSelector,
            topic1,
            topic2,
            topic3
        );
    }

    function _shortAddr(address a) internal pure returns (string memory) {
        bytes20 b = bytes20(a);
        bytes memory out = new bytes(4);
        out[0] = b[18];
        out[1] = b[19];
        out[2] = b[0];
        out[3] = b[1];
        return string(out);
    }
} ","keccak256":"0x115f1556eef2ff42e4d3391f6900fc652078f7819e0b54a566426a22660822bc","license":"MIT"},"contracts/portfolio/interfaces/IBalancerFactory.sol":{"content":"// SPDX-License-Identifier: MIT
